<?php			error_reporting(0);	if(isset($_POST["submit"]))	{		$name=$_POST["name"];		$id=$_POST["id"];		$pass=$_POST["pass"];		$password=$_POST["password"];		$user=$_POST["user"];		$admin=$_POST["admin"];						if (isset($name) && isset($id) && isset($pass) && isset($password) && (isset($user) || isset($admin)) )		{			if(($pass==$password) && strlen($password)>8 )			{				if(strlen($password)<8  )				{					echo "Password must be at least 8 character";				}								else if(strlen($password)==8)				{					for($i=0;$i<strlen($password);$i++)					{						if(!((($password[$i]>='A') && ($password[$i]<='Z')) || (($password[$i]>='a') && ($password[$i]<='z')) || (($password[$i]>='0') && ($password[$i]<='1')) ))														{								echo "password must be alpha neumaric";							}											}									}								if(($pass==$password) && strlen($password)>8)				{					$myfile = fopen("registratio.txt","a+");					fwrite($myfile,$name);					fwrite($myfile,',');					fwrite($myfile,$id);					fwrite($myfile,',');					fwrite($myfile,$password);					fwrite($myfile,',');					if(isset($user))					{						fwrite($myfile,$user);					}					else					{						fwrite($myfile,admin);					}										fwrite($myfile,"\r\n");															echo "<h2>Registration complete</h2>";				}											}			else{				echo "password not matched and password not less than 8 character";			}														}		else{						echo "Validate please";		}		}?><center><form action="" method="post">	<table border="0" cellspacing="0" cellpadding="0">		<tr>			<td>				<fieldset>					<legend><h3>REGISTRATION</h3></legend>					Id<br/><input type="text" name="id"><br/>					Password<br/><input  type="password" name="pass"><br/>					Confirm Password<br/><input type="password" name="password"><br/>					Name<br/><input type="text" name="name"><br/>					User Type<hr/>					<input name="user" type="radio" value="user" />User					<input name="admin" type="radio" value="admin" />Admin					<hr/>					<input type="submit" name="submit" value="Sign Up">					<a href="login.php">Sign In</a>				</fieldset>			</td>		</tr>                                	</table></form></center>		